import 'package:flutter/material.dart';import '../Widget/custom_button.dart';import '../Widget/custom_text_field.dart';class AddScreen extends StatefulWidget {  const AddScreen({super.key});  @override  State<AddScreen> createState() => _AddScreenState();}TextEditingController _title = TextEditingController();TextEditingController _detail = TextEditingController();class _AddScreenState extends State<AddScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: const Color(0xff9395D3),        title: const Text(          "add Task",          style: TextStyle(            color: Colors.white,            fontSize: 24,            fontWeight: FontWeight.w600,          ),        ),      ),      body: ListView(        padding: const EdgeInsets.all(20),        children: [          CustomTextField(            controller: _title,            hint: "Title",          ),          const SizedBox(            height: 20,          ),          CustomTextField(            controller: _detail,            hint: "Detail",          ),          const SizedBox(            height: 30,          ),          CustomButton(            onPressed: () {},            text: "Add",          )        ],      ),    );  }}