import 'package:flutter/material.dart';class CustomButton extends StatelessWidget {  final String text;  final VoidCallback onPressed;  final ButtonStyle? style;  const CustomButton({    super.key,    required this.text,    required this.onPressed,    this.style,  });  @override  Widget build(BuildContext context) {    return ElevatedButton(      style: style ??          const ButtonStyle(            padding: WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),            backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),          ),      onPressed: onPressed,      child: Text(        text,        style: const TextStyle(            fontSize: 20, fontWeight: FontWeight.w600, color: Colors.white),      ),    );  }}