import 'package:flutter/material.dart';class CustomTextField extends StatelessWidget {  final String hint;  final TextEditingController controller;  const CustomTextField({    super.key,    required this.controller,    required this.hint,  });  @override  Widget build(BuildContext context) {    return TextFormField(      controller: controller,      style: const TextStyle(color: Color(0xff8B8787)),      keyboardType: TextInputType.text,      decoration: InputDecoration(        hintText: hint,      ),    );  }}