import 'package:flutter/material.dart';class AddScreen extends StatefulWidget {  const AddScreen({super.key});  @override  State<AddScreen> createState() => _AddScreenState();}TextEditingController _title = TextEditingController();TextEditingController _detail = TextEditingController();class _AddScreenState extends State<AddScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: const Color(0xff9395D3),        title: const Text(          "add Task",          style: TextStyle(            color: Colors.white,            fontSize: 24,            fontWeight: FontWeight.w600,          ),        ),      ),      body: ListView(        padding: const EdgeInsets.all(20),        children: [          TextFormField(            controller: _title,            style: const TextStyle(color: Color(0xff8B8787)),            keyboardType: TextInputType.text,            decoration: const InputDecoration(              hintText: "Title",            ),          ),          const SizedBox(            height: 20,          ),          TextFormField(            controller: _detail,            style: const TextStyle(color: Color(0xff8B8787)),            keyboardType: TextInputType.text,            decoration: const InputDecoration(              hintText: "Detail",            ),          ),          const SizedBox(            height: 30,          ),          ElevatedButton(            style: const ButtonStyle(              padding: WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),              backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),            ),            child: const Text(              "ADD",              style: TextStyle(                  fontSize: 20,                  fontWeight: FontWeight.w600,                  color: Colors.white),            ),            onPressed: () {},          )        ],      ),    );  }}