import 'package:flutter/material.dart';import 'Widget/custom_button.dart';import 'Widget/custom_text_field.dart';class EditScreen extends StatefulWidget {  const EditScreen({super.key});  @override  State<EditScreen> createState() => _EditScreenState();}TextEditingController _title = TextEditingController();TextEditingController _detail = TextEditingController();class _EditScreenState extends State<EditScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: const Color(0xff9395D3),        title: const Text(          "Edite Task",          style: TextStyle(            color: Colors.white,            fontSize: 24,            fontWeight: FontWeight.w600,          ),        ),      ),      body: ListView(        padding: const EdgeInsets.all(20),        children: [          CustomTextField(            controller: _title,            hint: "Title",          ),          const SizedBox(            height: 20,          ),          CustomTextField(            controller: _detail,            hint: "Detail",          ),          const SizedBox(            height: 30,          ),          Row(            children: [              Expanded(                child: CustomButton(                  style: const ButtonStyle(                    padding:                        WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),                    backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),                  ),                  text: "Update",                  onPressed: () {},                ),              ),              const SizedBox(                width: 30,              ),              Expanded(                child: CustomButton(                  style: const ButtonStyle(                    padding:                        WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),                    backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),                  ),                  text: "Cancel",                  onPressed: () {},                ),              ),            ],          )        ],      ),    );  }}