import 'package:flutter/material.dart';class EditScreen extends StatefulWidget {  const EditScreen({super.key});  @override  State<EditScreen> createState() => _EditScreenState();}TextEditingController _title = TextEditingController();TextEditingController _detail = TextEditingController();class _EditScreenState extends State<EditScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      appBar: AppBar(        backgroundColor: const Color(0xff9395D3),        title: const Text(          "Edite Task",          style: TextStyle(            color: Colors.white,            fontSize: 24,            fontWeight: FontWeight.w600,          ),        ),      ),      body: ListView(        padding: const EdgeInsets.all(20),        children: [          TextFormField(            controller: _title,            style: const TextStyle(color: Color(0xff8B8787)),            keyboardType: TextInputType.text,            decoration: const InputDecoration(              hintText: "Title",            ),          ),          const SizedBox(            height: 20,          ),          TextFormField(            controller: _detail,            style: const TextStyle(color: Color(0xff8B8787)),            keyboardType: TextInputType.text,            decoration: const InputDecoration(              hintText: "Detail",            ),          ),          const SizedBox(            height: 30,          ),          Row(            children: [              Expanded(                child: ElevatedButton(                  style: const ButtonStyle(                    padding:                        WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),                    backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),                  ),                  child: const Text(                    "Update",                    style: TextStyle(                        fontSize: 20,                        fontWeight: FontWeight.w600,                        color: Colors.white),                  ),                  onPressed: () {},                ),              ),              const SizedBox(                width: 30,              ),              Expanded(                child: ElevatedButton(                  style: const ButtonStyle(                    padding:                        WidgetStatePropertyAll(EdgeInsetsDirectional.all(15)),                    backgroundColor: WidgetStatePropertyAll(Color(0xff9395D3)),                  ),                  child: const Text(                    "Cancel",                    style: TextStyle(                        fontSize: 20,                        fontWeight: FontWeight.w600,                        color: Colors.white),                  ),                  onPressed: () {},                ),              ),            ],          )        ],      ),    );  }}