import 'package:flutter/material.dart';import 'package:todo/completed_screen.dart';import 'package:todo/tasks_screen.dart';import 'add_screen.dart';class MainScreen extends StatefulWidget {  const MainScreen({super.key});  @override  State<MainScreen> createState() => _MainScreenState();}int _currentIndex = 0;final List<Widget> _screens = [  const AllTask(),  const CompletedScreen(),];class _MainScreenState extends State<MainScreen> {  @override  Widget build(BuildContext context) {    return Scaffold(      backgroundColor: const Color(0xffD6D7EF),      body: _screens[_currentIndex],      floatingActionButton: const BuildFloatingActionButton(),      bottomNavigationBar: buildBottomNavigationBar(),    );  }  BottomNavigationBar buildBottomNavigationBar() {    return BottomNavigationBar(      items: const [        BottomNavigationBarItem(icon: Icon(Icons.menu_sharp), label: "All"),        BottomNavigationBarItem(            icon: Icon(Icons.check_rounded), label: "Completed"),      ],      onTap: (newIndex) {        setState(() {          _currentIndex = newIndex;        });      },      currentIndex: _currentIndex,    );  }}/////////////////////////////////////////////////////////////////class BuildFloatingActionButton extends StatelessWidget {  const BuildFloatingActionButton({    super.key,  });  @override  Widget build(BuildContext context) {    return Container(      clipBehavior: Clip.antiAliasWithSaveLayer,      decoration:          const BoxDecoration(shape: BoxShape.circle, color: Color(0xff9395D3)),      child: IconButton(        padding: const EdgeInsets.all(15),        color: Colors.white,        onPressed: () {          Navigator.push(              context,              MaterialPageRoute(                builder: (context) => const AddScreen(),              ));        },        icon: const Icon(Icons.add),      ),    );  }}